$(function(){  
    $($('[id=toAdd]')).click(function(){  
	    var p = $(this).attr("systemidvalue");
	    var url = "http://127.0.0.1:7890/add/"+p+".html"
	    var request = new XMLHttpRequest();
	    request.open("GET", url);
	    request.send(null);
    });
    $($('[id=toDel]')).click(function(){  
	    var p = $(this).attr("systemidvalue");
	    var url = "http://127.0.0.1:7890/del/"+p+".html"
	    var request = new XMLHttpRequest();
	    request.open("GET", url);
	    request.send(null);
    });
    $($('[id=update]')).click(function(){  
	    var p1 = $('.label2').text();
	    var p2 = $('.txt').val();
	    var url = "http://127.0.0.1:7890/update/"+p1+"/"+p2+".html"
	    var request = new XMLHttpRequest();
	    request.open("GET", url);
	    request.send(null);
    });
});  